using System;namespace Sita.APC.Toolset.Core.Exceptions{    [Serializable]    public class InvalidFieldException : Exception    {        public InvalidFieldException() { }        public InvalidFieldException(string message) : base(message)        {            System.Console.WriteLine(message);        }        public InvalidFieldException(            string message, System.Exception inner        ) : base(message, inner)        {            System.Console.WriteLine(                string.Format(                    "Message: {0}\nInnerException.Message: {1}",                    message,                    inner.Message                )            );        }        protected InvalidFieldException(            System.Runtime.Serialization.SerializationInfo info,            System.Runtime.Serialization.StreamingContext context        ) : base(info, context) { }    }}
