namespace Sita.APC.Toolset.Domain.DataTransfer{    public class RequestResult    {        public ResultType ResultType { get; private set; }        public string ResultTypeDescription { get; private set; }        public string Message { get; private set; }        public object CreatedObject { get; private set; }        public RequestResult(            ResultType resultType,            string message,            object createdObject        )        {            ResultType = resultType;            Message = message;            ResultTypeDescription = resultType.ToString();            CreatedObject = createdObject;        }    }    public enum ResultType    {        Success,        InvalidField,        DuplicatedItem,        UnknownFailure    }}
