using Sita.APC.Toolset.Core.Database;using Sita.APC.Toolset.Domain.Interfaces;using System.Collections.Generic;using System.ComponentModel.DataAnnotations;namespace Sita.APC.Toolset.Domain.Models{    public class BoardingPass : ModelBase, IBoardingPass    {        public string BoardingPassId { get; set; }        // ###    Public Props        // Generated Information        public string BarCodeString { get; set; }        public string Project { get; set; }        public string BarCodeStringLenght { get; set; }        public string JulianDateOfIssueOfBoardingPass { get; set; }        public string BaggageTagLicencePlateNumber { get; set; }        public string FirstNonConsecutiveBaggageTagLicencePlateNumber { get; set; }        public string SecondNonConsecutiveBaggageTagLicencePlateNumber { get; set; }        public string FieldSizeOfFollowingStructuredMessageUnique { get; set; }        public int ConditionalUniqueFieldsSize { get; set; }        public string PassengerNameInBoardingPass { get; set; }        // Mandatory items - Inherent to Boarding Pass        public string FormatCode { get; set; }        public string NumberOfLegs { get; set; }        [StringLength(32)]        public string PassengerFirstName { get; set; }        [StringLength(32)]        public string PassengerLastName { get; set; }                public string ElectronicTicketIndicator { get; set; }        [Required]        public List<Leg> Legs { get; set; }        // Conditional items - Inherent to Boarding Pass        public string BeginningOfVersionNumber { get; set; }        public string VersionNumber { get; set; }        public string PassengerDescription { get; set; }        public string SourceOfCheckIn { get; set; }        public string SourceOfBoardingPassIssuance { get; set; }        public string DateOfIssueOfBoardingPass { get; set; }        public string DocumentType { get; set; }        public string AirlineDesignatorOfBoardingPassIssuer { get; set; }        [Required]        public List<Bag> Bags { get; set; }    }}
