using Microsoft.EntityFrameworkCore;using Sita.APC.Toolset.Domain.Models;namespace Sita.APC.Toolset.Repository.Database{    public class BoardingPassContext : DbContext    {        public DbSet<BoardingPass>  BoardingPasses { get; set; }        public DbSet<Leg> Legs { get; set; }        public DbSet<Bag> Bags { get; set; }        public BoardingPassContext(DbContextOptions<BoardingPassContext> options) : base(options) { }                protected override void OnModelCreating(ModelBuilder modelBuilder)        {            modelBuilder.Entity<Leg>( entity => entity.HasKey(l => l.LegId) );            modelBuilder.Entity<Bag>( entity => entity.HasKey(b => b.BagId) );            modelBuilder.Entity<BoardingPass>(                entity =>                {                    entity.HasKey(bp => bp.BoardingPassId);                    entity.HasMany<Leg>(bp => bp.Legs)                    .WithOne()                    .IsRequired();                    entity.HasMany<Bag>(bp => bp.Bags)                    .WithOne()                    .IsRequired();                }            );        }    }}
