using Microsoft.Extensions.DependencyInjection;using Sita.APC.Toolset.Domain.Models;using Sita.APC.Toolset.Services.API;using Sita.APC.Toolset.Services.Interfaces;namespace Sita.APC.Toolset.Services.Middleware{    public static class ServicesMiddleware    {        public static void AddServicesDependency(this IServiceCollection services)        {            services.AddSingleton<ILegStandard, LegStandard>();            services.AddScoped<IBoardingPassStandard, BoardingPassStandard>(); // It's necessary to be scoped because of First and Second NonConsecutiveBaggageTagLicencePlateNumberExpectedSize fields in bar code which is set in runtime. Check the model BoardingPassStandard for details.            services.AddScoped<IBoardingPassService, BoardingPassService>();            services.AddScoped<IBagService, BagService>();            services.AddScoped<ILegService, LegService>();            services.AddTransient<IApplicationProgrammingInterface<BoardingPass>, BoardingPassAPI>();        }    }}
